insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (1, 2, 'Latvia', 'Путеводитель по Латвии. Полная информация о стране для туриста. ', 'Латвия, туризм, климат, транспорт, полезная информация', 'Латвия (Latvijas Republika), столица – Рига (Riga), официальный язык – латышский, месторасположение – северная Европа, денежная единица – евро (€). ', 'Латвия', '', 'a:8:{i:0;a:2:{s:4:\"name\";s:67:\"Сигулдский замок Ливонского ордена. \";s:4:\"link\";s:87:\"http://lions-guides.ru/Latvia/interesting-places-of-Sigulda/Sigulda-castle-of-the-Livon\";}i:1;a:2:{s:4:\"name\";s:34:\"Сигулдский дворец.\";s:4:\"link\";s:78:\"http://lions-guides.ru/Latvia/interesting-places-of-Sigulda/new-Sigulda-Palace\";}i:2;a:2:{s:4:\"name\";s:34:\"Национальный парк \";s:4:\"link\";s:79:\"http://lions-guides.ru/Latvia/interesting-places-of-Sigulda/Gauja-national-Park\";}i:3;a:2:{s:4:\"name\";s:38:\"Домский собор в Риге.\";s:4:\"link\";s:71:\"http://lions-guides.ru/Latvia/interesting-places-of-Riga/dome-cathedral\";}i:4;a:2:{s:4:\"name\";s:43:\"Церковь св.Петра в Риге.\";s:4:\"link\";s:79:\"http://lions-guides.ru/Latvia/interesting-places-of-Riga/the-Church-of-St-Peter\";}i:5;a:2:{s:4:\"name\";s:26:\"Вантовый мост.\";s:4:\"link\";s:62:\"http://lions-guides.ru/Latvia/nteresting-places-of-Riga/bridge\";}i:6;a:2:{s:4:\"name\";s:26:\"Рижский замок.\";s:4:\"link\";s:68:\"http://lions-guides.ru/Latvia/interesting-places-of-Riga/Riga-castle\";}i:7;a:2:{s:4:\"name\";s:33:\"Бастионная горка. \";s:4:\"link\";s:69:\"http://lions-guides.ru/Latvia/interesting-places-of-Riga/Bastion-hill\";}}', 'a:2:{i:0;a:2:{s:4:\"name\";s:45:\"Путеводитель по Сигулде.\";s:4:\"link\";s:44:\"http://lions-guides.ru/Latvia/guides/Sigulda\";}i:1;a:2:{s:4:\"name\";s:39:\"Путеводитель по Риге.\";s:4:\"link\";s:41:\"http://lions-guides.ru/Latvia/guides/Riga\";}}', '<h3 style=\"text-align: center;\"><span style=\"font-size: 14pt; font-family: \'arial black\', \'avant garde\'; color: #000000;\">Путеводитель по Латвии</span></h3>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (2, 4, 'Israel', ' Путеводитель по Израилю. Полная информация о стране для туриста. ', 'Израиль, туризм, море, транспорт, безопасность, религия', 'Государство Израиль (מדינת ישראל). Столица - г.Иерусалим. Государственные языки: иврит, арабский. Валюта - новый шейкель (₪). Население - 8,25 миллионов человек. ', 'Израиль', '', 'a:10:{i:0;a:2:{s:4:\"name\";s:21:\"Неве-Цедек. \";s:4:\"link\";s:72:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/Neve-Tzedek\";}i:1;a:2:{s:4:\"name\";s:9:\"Яффо.\";s:4:\"link\";s:66:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/Jaffa\";}i:2;a:2:{s:4:\"name\";s:25:\"Шук-а-Кармель.\";s:4:\"link\";s:74:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/SHUK-a-Carmel\";}i:3;a:2:{s:4:\"name\";s:22:\"Стена Плача.\";s:4:\"link\";s:74:\"http://lions-guides.ru/Israel/interesting-places-of-Jerusalem/Wailing-wall\";}i:4;a:2:{s:4:\"name\";s:31:\"Пляжи Тель-Авива.\";s:4:\"link\";s:84:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/the-beaches-of-Tel-Aviv\";}i:5;a:2:{s:4:\"name\";s:25:\"Мини-Израиль. \";s:4:\"link\";s:72:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/mini-Israel\";}i:6;a:2:{s:4:\"name\";s:61:\"Обзорная экскурсия по Тель-Авиву.\";s:4:\"link\";s:77:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/tour-of-Tel-Aviv\";}i:7;a:2:{s:4:\"name\";s:60:\"Художественный музей Тель-Авива.\";s:4:\"link\";s:71:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/art-museum\";}i:8;a:2:{s:4:\"name\";s:45:\"Сафари-Парк в Рамат Гане.\";s:4:\"link\";s:85:\"http://lions-guides.ru/Israel/interesting-places-of-Tel-Aviv/Safari-Park-in-Ramat-Gan\";}i:9;a:2:{s:4:\"name\";s:48:\"Яд Вашем - мемориал памяти.\";s:4:\"link\";s:72:\"http://lions-guides.ru/Israel/interesting-places-of-Jerusalem/Yad-Vashem\";}}', 'a:2:{i:0;a:2:{s:4:\"name\";s:50:\"Путеводитель по Тель-Авиву.\";s:4:\"link\";s:45:\"http://lions-guides.ru/Israel/guides/Tel-Aviv\";}i:1;a:2:{s:4:\"name\";s:52:\"Путеводитель по Иерусалиму. \";s:4:\"link\";s:46:\"http://lions-guides.ru/Israel/guides/Jerusalem\";}}', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em><img src=\"/uploads/spool/Флаг_Израиля.jpg\" alt=\"\" width=\"300\" height=\"199\" />Израиль</em>&nbsp;- крохотная точка на карте, маленький кусочек мира, но как много он вмещает в себя параллельных миров! Сюда можно ездить бесконечно, в любое время года, и каждый раз Вы&nbsp;будете находить&nbsp;новые, яркие грани. </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"> Колыбель трех религий, святая земля, хранящая тайну сотворения мира, места, которые буквально дышат древнейшей историей человечества: Храм Гроба Господнего, Церковь Рождества, Стена Плача, мечеть Аль Акса. Они не подвластны времени. Здесь соединяется прошлое, настоящее и будущее, и даже человека неверующего охватывает благоговейное восхищение и просветление. </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"> Спокойное Мертвое море вобрало в себя столько соли, что уже больше ничего не может вобрать, и так и норовит вытолкнуть вас из себя. Здесь вы получите порцию оздоровительных процедур, заряд бодрости, оптимизма и душевного спокойствия до следующего отпуска. Идеальные для дайвинга кристально чистые и прозрачные воды Красного моря с коралловыми рифами и многообразной флорой и фауной. Средиземное море с бурной ночной жизнью, пляжными дискотеками и разноцветными огнями кафешек и ресторанов. Пресноводное Галилейское море, а фактически озеро Кинерет, славится своими горячими источниками и превосходной рыбалкой (здесь обитает 22 вида рыб). Кроме того, именно в этих краях Иисус Христос творил свои первые чудеса. </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"> Пальмы и огромные кактусы сменяются дубами и соснами, а те в свою очередь кипарисами и олеандрами. Абрикосы, персики, черешня, шелковица, гранаты, финики, инжир, мандарины, свити, маракуйя, карамбола, личи - вот небольшой перечень того райского разнообразия, который можно встретить на прилавках израильских магазинов и рынков. Причем, благодаря тому, что урожай получают несколько раз в год, свежие фрукты там постоянно. </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"> Шумные, доброжелательные и веселые люди. Они улыбаются друг другу на улице, громко разговаривают и бурно жестикулируют.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em> Израиль</em>&nbsp;- многолик. Это бесценный клад для человека с богатым воображением и открытой душой. Впустите его в свое сердце, и он отблагодарит вас зарядом бодрости, оптимизма и душевного спокойствия. Шалом, <em>Израиль</em>!</span></p>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (4, 1, 'Tatarstan', 'Татарстан. Информация о республике. ', 'республика Татарстан, местоположение, население, государственный флаг, города', 'Республика Татарстан. Столица - Казань. Население - 3 850 000 человек. Язык - татарский, русский. Время + 0 к МСК,', 'Татарстан', '', 'a:7:{i:0;a:2:{s:4:\"name\";s:32:\"Казанский Кремль.\";s:4:\"link\";s:68:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/Kremlin\";}i:1;a:2:{s:4:\"name\";s:51:\"Музей естественной истории.\";s:4:\"link\";s:86:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/museum-of-natural-history\";}i:2;a:2:{s:4:\"name\";s:38:\"Парк Победы в Казани.\";s:4:\"link\";s:73:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/Victory-Park\";}i:3;a:2:{s:4:\"name\";s:59:\"Губернаторский дворец в Казани. \";s:4:\"link\";s:78:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/Governor\'s-Palace\";}i:4;a:2:{s:4:\"name\";s:38:\"Сад Эрмитаж в Казани.\";s:4:\"link\";s:77:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/Hermitage-garden\";}i:5;a:2:{s:4:\"name\";s:59:\"Спасо-Преображенский монастырь.\";s:4:\"link\";s:81:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/Spaso-Preobrazhensky\";}i:6;a:2:{s:4:\"name\";s:26:\"Пушечный двор.\";s:4:\"link\";s:72:\"http://lions-guides.ru/Tatarstan/interesting-places-of-Kazan/Cannon-yard\";}}', 'a:2:{i:0;a:2:{s:4:\"name\";s:70:\"Путеводитель по Республике Татарстан.\";s:4:\"link\";s:61:\"http://lions-guides.ru/Tatarstan/guides/Republic-of-Tatarstan\";}i:1;a:2:{s:4:\"name\";s:43:\"Путеводитель по Казани.\";s:4:\"link\";s:45:\"http://lions-guides.ru/Tatarstan/guides/Kazan\";}}', '<h3 style=\"text-align: center;\"><span style=\"color: #000000; font-family: \'arial black\', \'avant garde\';\"><span style=\"font-size: 12pt;\">Республика Татарстан</span></span></h3>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (5, 1, 'Yaroslavl-region', 'Путеводитель по Ярославской области. Отдых. Туризм. Описание городов. ', 'Ярославль, города, история, Рыбинск, Углич, Переславль, Ростов Великий, Мышкин', 'Ярославская область привлекательная для туристов желающих приобщиться к истории Древней Руси, познакомиться с старинными Русскими городами, посетить вековые монастыри и церкви. ', 'Ярославская область', '', 'a:4:{i:0;a:2:{s:4:\"name\";s:70:\"Ярославский кремль или Рубленый город\";s:4:\"link\";s:79:\"http://lions-guides.ru/Yaroslavl-region/interesting-places-of-Yaroslavl/Kremlin\";}i:1;a:2:{s:4:\"name\";s:39:\"Волжская набережная. \";s:4:\"link\";s:87:\"http://lions-guides.ru/Yaroslavl-region/interesting-places-of-Yaroslavl/Volga-embankmen\";}i:2;a:2:{s:4:\"name\";s:59:\"Спасо-Преображенский монастырь.\";s:4:\"link\";s:81:\"http://lions-guides.ru/Yaroslavl-region/interesting-places-of-Yaroslavl/monastery\";}i:3;a:2:{s:4:\"name\";s:61:\"Ярославский драматический театр.\";s:4:\"link\";s:85:\"http://lions-guides.ru/Yaroslavl-region/interesting-places-of-Yaroslavl/drama-theatre\";}}', 'a:1:{i:0;a:2:{s:4:\"name\";s:48:\"Путеводитель по Ярославлю\";s:4:\"link\";s:56:\"http://lions-guides.ru/Yaroslavl-region/guides/Yaroslavl\";}}', '<p>&nbsp;</p>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (6, 1, 'Samara-region', 'Путеводитель по Самарской области. Туризм. Описание. ', 'Самарская область, города самарской области, села самарской области, ', 'Самарская область - один из туристических центров Поволжья. Каждый путешественник составит свой маршрут по интересам, ведь самарской области есть что предложить. ', 'Самарская область', '', 'a:0:{}', 'a:0:{}', '<h2 style=\"text-align: center;\"><span style=\"font-size: 12pt; font-family: \'arial black\', \'avant garde\'; color: #000000;\">Самарская область. Общая информация.&nbsp;</span></h2>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (7, 3, 'Egypt', 'Египет. Информация для туристов. Курорты.', 'Египет, история, климат, транспорт, курорты', 'Египет - самая многочисленная страна арабского мира (население - 80 миллионов человек). Египет расположен на севере Африки и не большая его часть в Азии (Аравийский полуостров).', 'Египет', '', 'a:3:{i:0;a:2:{s:4:\"name\";s:18:\"Наама-бей.\";s:4:\"link\";s:76:\"http://lions-guides.ru/Egypt/interesting-places-of-Sharm-El-Sheikh/Naama-bay\";}i:1;a:2:{s:4:\"name\";s:54:\"Старый город в Шарм-эль-Шейхе.\";s:4:\"link\";s:66:\"http://lions-guides.ru/Egypt/interesting-places-of-Sharm-El-Sheikh\";}i:2;a:2:{s:4:\"name\";s:30:\"Остров фараонов.\";s:4:\"link\";s:83:\"http://lions-guides.ru/Egypt/interesting-places-of-Sharm-El-Sheikh/Pharoah\'s-island\";}}', 'a:2:{i:0;a:2:{s:4:\"name\";s:43:\"Путеводитель по Египту.\";s:4:\"link\";s:58:\"http://lions-guides.ru/Egypt/guides/Arab-Republic-of-Egypt\";}i:1;a:2:{s:4:\"name\";s:45:\"Путеводитель по Хургаде.\";s:4:\"link\";s:44:\"http://lions-guides.ru/Egypt/guides/Hurghada\";}}', '<p><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\"><em><img src=\"/uploads/spool/герб1.jpg\" alt=\"\" width=\"158\" height=\"221\" /><img src=\"/uploads/spool/Флаг-Египта.jpg\" alt=\"\" width=\"321\" height=\"214\" />Египет</em> &ndash; крупнейшее арабское государство, расположенное на севере Африки, и небольшой частью - на Синайском полуострове Азии, вследствие чего называемой страной двух материков. Это удивительная страна, имеющая богатейшую историю и культуру, сыгравшая важнейшую роль в мировых исторических процессах.</span><br /><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\">Сегодня <em>Египет</em> представляет собой направление, популярное среди <em>туристов</em> из всего мира. И это неудивительно! Ведь здесь есть все возможности для комфортного, яркого и интересного <em>отдыха</em>: шикарные отели и рестораны, превосходные песчаные пляжи, потрясающе красивый подводный мир Красного моря, интереснейшие экскурсии по значимым историческим местам, увлекательные сафари по пустыне, круизы по реке Нил и много другого, что подарит массу положительных эмоций и впечатлений.</span></p>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (8, 1, 'Crimea', 'Путеводитель по Крыму -  климат, транспорт, как добраться, общая информация. ', 'Крым, Евпатория, Севастополь, Черное море, здравница', 'Крым - полуостров уникален тем, что на его территории представлены несколько климатических зон, что дает возможность каждому путешественнику выбрать для себя подходящую. ', 'Крым', '', 'a:2:{i:0;a:2:{s:4:\"name\";s:51:\"Текие дервишей в Евпатории. \";s:4:\"link\";s:77:\"http://lions-guides.ru/Crimea/interesting-places-of-Evpatoria/tekie-dervishes\";}i:1;a:2:{s:4:\"name\";s:35:\"Караимские кенасы. \";s:4:\"link\";s:76:\"http://lions-guides.ru/Crimea/interesting-places-of-Evpatoria/Karaite-kenasa\";}}', 'a:3:{i:0;a:2:{s:4:\"name\";s:39:\"Путеводитель по Ялте.\";s:4:\"link\";s:42:\"http://lions-guides.ru/Crimea/guides/Yalta\";}i:1;a:2:{s:4:\"name\";s:44:\"Путеводитель по Судаку. \";s:4:\"link\";s:42:\"http://lions-guides.ru/Crimea/guides/Sudak\";}i:2;a:2:{s:4:\"name\";s:49:\"Путеводитель по Евпатории.\";s:4:\"link\";s:46:\"http://lions-guides.ru/Crimea/guides/Evpatoria\";}}', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em><img src=\"/uploads/spool/крым6.jpg\" alt=\"\" width=\"204\" height=\"306\" />Крым</em> наполнен природными, архитектурными и историческими достопримечательностями. В каждом уголке полуострова туристу есть, на что посмотреть и провести отпуск в свое удовольствие. Морские прогулки с купанием в открытом море и ночной дискотекой на палубе, катание на лошадях, подъем на горы, прогулки по целебным можжевеловым рощам и заповедным горным лесам, купание в водопаде, поход к чудодейственному роднику, дайвинг, дегустация знаменитых крымских вин, шампанского и коньяка. Для детей тоже найдутся развлечения: ялтинский зоопарк, аквапарки, дельфинарий, океанариум, разнообразные аттракционы в парках. Активное времяпрепровождение на берегу: парасейлинг, водные горки, катание на банане, таблетке, водном скутере. Можно воспользоваться услугами местных туристических фирм, которые предлагают богатую экскурсионную и развлекательную программу, а экскурсоводы целый день будут рассказывать, показывать, отвечать на все вопросы, следить, чтобы никто не потерялся. Ведь, это люди, беззаветно любящие Крым, и готовые делиться своей любовью с нами.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Здесь отдых на любой вкус. Можно снять домик в частном секторе или квартиру в центре, поселиться в пансионате или отеле с высоким уровнем комфорта, бассейном и трехразовым питанием, что немаловажно, когда отдыхаешь с маленькими детьми. Можно остановиться в горном селе вдали от надоевшей за год городской суеты и наслаждаться спокойствием в уединении с природой. Или в курортном городе с оживленными набережными, развлекательными центрами, аттракционами, кафе и барами, ночными дискотеками.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img src=\"/uploads/spool/крым0022.jpg\" alt=\"\" width=\"304\" height=\"202\" />Отдых в <em>Крыму</em> - это возможность поправить свое здоровье. Крымский воздух, по сути, является уникальным природным ингалятором, обладающим целительной силой. Он насыщен отрицательными аэроионами, которые ученые называют \"воздушными витаминами\", морскими солями, фитонцидами и эфирными маслами, которые щедро выделяют розмарин, лаванда, сосна, ель, можжевельник и другие растения. На полуострове функционируют около шестисот профильных здравниц и санаториев, предлагающие различные оздоровительные программы.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Мягкий климат, много солнца, которого нам так не хватает, чистый воздух, красивая природа, живописные скалистые бухты, песчаные и галечные пляжи, ласково плещущееся теплое море, сезонные спелые сочные фрукты и ягоды. Есть в нашей стране места настолько прекрасные, что каждое их посещение вызывает чувство восхищения и желание вернуться. И одно из таких мест - солнечный <em>Крым</em>.</span></p>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (9, 2, 'Belarus', 'Республика Беларусь. Полная информация для туриста. ', 'Республика Беларусь, Минск, Витебск, Могилев, Брест', 'Республика Беларусь - государство в Восточной Европе, площадь - 207 600 кв.км.,  столица - Минск, население 9,5 миллионов человек.', 'Республика Беларусь', '', 'a:5:{i:0;a:2:{s:4:\"name\";s:58:\"Площадь независимости в Минске.\";s:4:\"link\";s:78:\"http://lions-guides.ru/Belarus/interesting-places-of-Minsk/independence-square\";}i:1;a:2:{s:4:\"name\";s:7:\"ГУМ.\";s:4:\"link\";s:71:\"http://lions-guides.ru/Belarus/interesting-places-of-Minsk/GUM-in-Minsk\";}i:2;a:2:{s:4:\"name\";s:46:\"Минская городская ратуша\";s:4:\"link\";s:68:\"http://lions-guides.ru/Belarus/interesting-places-of-Minsk/town-hall\";}i:3;a:2:{s:4:\"name\";s:63:\"Национальная библиотека Беларуси.\";s:4:\"link\";s:66:\"http://lions-guides.ru/Belarus/interesting-places-of-Minsk/library\";}i:4;a:2:{s:4:\"name\";s:27:\"Костел Св.Роха.\";s:4:\"link\";s:79:\"http://lions-guides.ru/Belarus/interesting-places-of-Minsk/Church-of-Saint-Roch\";}}', 'a:1:{i:0;a:2:{s:4:\"name\";s:44:\"Путеводитель по Минску. \";s:4:\"link\";s:43:\"http://lions-guides.ru/Belarus/guides/Minsk\";}}', '<h2 style=\"text-align: center;\"><span style=\"font-size: 14pt; font-family: \'arial black\', \'avant garde\'; color: #000000;\">Путеводитель по Республике Беларусь</span></h2>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (10, 1, 'Krasnodar-krai', 'Путеводитель по Краснодарскому краю. Информация для туристов.', 'Краснодарский край, транспорт, достопримечательности, климат, транспортное сообщение', 'Краснодарский край - уникальный туристический регион России, способный предложить своим гостям самый разнообразный отдых: от горнолыжных курортов, до пляжей мирового уровня.', 'Краснодарский край', '', 'a:0:{}', 'a:1:{i:0;a:2:{s:4:\"name\";s:39:\"Путеводитель по Сочи.\";s:4:\"link\";s:50:\"http://lions-guides.ru/Krasnodar-krai/guides/Sochi\";}}', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Если есть на свете рай - это <em>Краснодарский край</em>. И это неоспоримая истина для более пяти миллионов человек, проживающих на юго-западе Северного Кавказа в районе сорок пятой параллели.&nbsp;</span><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Если вы сами желаете в этом убедиться, то хлебосольная Кубань будет рада каждому гостю.</span></p>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (11, 2, 'Italy', 'Италия. Полезная информация для туристов. Советы туристам. ', 'Италия, климат, полезные телефоны, аренда автомобиля, питание в Италии, шоппинг', 'Италия - страна, в которой для туристов не бывает мертвых сезонов: летом - пляжный отдых, зимой - посещение музеев и достопримечательностей. Природа, кухня, шоппинг хороши в любой сезон.', 'Италия', '', 'a:6:{i:0;a:2:{s:4:\"name\";s:38:\"Собор Святого Петра. \";s:4:\"link\";s:75:\"http://lions-guides.ru/Italy/interesting-places-of-Rome/St-Peter\'s-Basilica\";}i:1;a:2:{s:4:\"name\";s:15:\"Колизей.\";s:4:\"link\";s:65:\"http://lions-guides.ru/Italy/interesting-places-of-Rome/Colosseum\";}i:2;a:2:{s:4:\"name\";s:24:\"Дворец Дожей.\";s:4:\"link\";s:75:\"http://lions-guides.ru/Italy/interesting-places-of-Venice/the-Doge\'s-Palace\";}i:3;a:2:{s:4:\"name\";s:22:\"Гранд-Канал.\";s:4:\"link\";s:73:\"http://lions-guides.ru/Italy/interesting-places-of-Venice/the-Grand-canal\";}i:4;a:2:{s:4:\"name\";s:35:\"Фонтан Четырех рек.\";s:4:\"link\";s:83:\"http://lions-guides.ru/Italy/interesting-places-of-Rome/fountain-of-the-four-rivers\";}i:5;a:2:{s:4:\"name\";s:38:\"Собор Святого Марка. \";s:4:\"link\";s:76:\"http://lions-guides.ru/Italy/interesting-places-of-Venice/St-Marks-Cathedral\";}}', 'a:1:{i:0;a:2:{s:4:\"name\";s:39:\"Путеводитель по Риму.\";s:4:\"link\";s:40:\"http://lions-guides.ru/Italy/guides/Rome\";}}', '<h2 style=\"text-align: center;\"><span style=\"font-size: 14pt; font-family: arial, helvetica, sans-serif; color: #003366;\">Италия - информация о стране</span></h2>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (12, 1, 'Kostroma-region', 'Костромская область. Путеводитель: информация о городах, селах.', 'Костромская область, Кострома, Галич, Солигалич, Кологрив, Чухлома, Нерехта, Нея, Волгореченск', 'Костромская область относится к традиционным русским областям средней полосы. Туристические маршруты по городам и селам Костромской области становятся все более популярны как среди самостоятельных путешественников, так и организованных туристических групп. ', 'Костромская область', '', 'a:1:{i:0;a:2:{s:4:\"name\";s:61:\"Ипатьевский монастырь В Костроме\";s:4:\"link\";s:87:\"http://lions-guides.ru/Kostroma-region/interesting-places-of-Kostroma/Ipatiev-monastery\";}}', 'a:1:{i:0;a:2:{s:4:\"name\";s:46:\"Путеводитель по Костроме\";s:4:\"link\";s:54:\"http://lions-guides.ru/Kostroma-region/guides/Kostroma\";}}', '<h2 style=\"text-align: center;\"><span style=\"font-size: 14pt; font-family: arial, helvetica, sans-serif; color: #003366;\">Общая информация о Костромской области</span></h2>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (13, 1, 'Altai-Krai', 'Путеводитель по Алтайскому краю. Информация для туриста.', 'Алтайский край, Алтай, туристические базы, необычный туризм, туризм в горном Алтае', 'Алтайский край - жемчужина туристических маршрутов России. Первозданная нетронутая природа - главное богатство Алтайского края. Наш путеводитель расскажет о всех видах и формах туризма на Алтае.  ', 'Алтайский край', '', 'a:0:{}', 'a:0:{}', '<p><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em><img src=\"/uploads/spool/алтай1.jpg\" alt=\"\" width=\"213\" height=\"320\" />Алтайский край</em> - магнит для туристов.&nbsp;</span><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Два контрастных мира - захватывающие своим величием горы и манящие равнины - удивительно сочетаются в <em>Алтайском крае</em>. Это сердце Евразийского континента, которое имеет огромную историко-культурную ценность. Путешественники облюбовали бескрайние степи и непроходимую тайгу, они предпочитают подниматься верх на дельтаплане, опускаться с аквалангом под воду и дышать ни с чем несравнимым воздухом около заповедных озер. </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">На территории <em>Алтайского края</em> расположена одна из самых больших равнин на планете Земля - Западно-Сибирская. Горы раскинулись далеко за пределами географического объекта и занимают часть граничащих с ним государств: Китай, Казахстан, Монголия. Такой природы больше нигде нет, ведь эти места нельзя ни с чем сравнить и даже описать словами, насколько они прекрасны. Если есть желание познать тайны девственно чистой природы, тщательно охраняемой от вторжения цивилизации, стоит провести отпуск в одном из наиболее красочных уголков России.</span></p>
insert into `tourism_country` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `image`, `lions`, `guides`, `description`) values (14, 4, 'Turkey', 'Путеводитель по Турции. Информация для туристов.', 'Турция, достопримечательности, климат, шоппинг, развлечения', 'Турция - полная информация о стране для туриста: описание достопримечательностей, климат, транспорт, развлечения, шоппинг и многое другое. ', 'Турция', '', 'a:1:{i:0;a:2:{s:4:\"name\";s:52:\"Залив Золотой рог в Стамбуле\";s:4:\"link\";s:72:\"http://lions-guides.ru/Turkey/interesting-places-of-Istanbul/Golden-horn\";}}', 'a:1:{i:0;a:2:{s:4:\"name\";s:46:\"Путеводитель по Стамбулу\";s:4:\"link\";s:45:\"http://lions-guides.ru/Turkey/guides/Istanbul\";}}', '<p><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt; color: #000000;\"><em><img src=\"/uploads/spool/21.jpg\" alt=\"\" width=\"233\" height=\"325\" />Турция</em> - одна из самых древних стран, обладающая богатейшим наследием. Расположена&nbsp;на территории Малой Азии и небольшой своей частью в юго-восточной Европе, поэтому это место уникального сочетания восточных традиций и европейской культуры. Славится гостеприимством, особым уютом и прекрасным сервисом. Да, <em>Турция</em> многих привлекает своей отлично развитой инфраструктурой: благоустроенными пляжами, отелями, анимацией, прибрежными барами и ресторанами &ndash; по системе \"все включено\". Но это еще не все! Одно из главных достоинств Турции - ее культурно-исторические памятники, которых здесь великое множество. Среди российских туристов - <em>Турция</em>, пожалуй, самый популярный курорт!</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (1, 1, 'Latvia/article/stay-in-Sigulda', 'Отдых в Сигулде.  ', 'Сигулда, Гауя,Турайдский музей-заповедник, национальный парк, горнолыжные трассы', 'Сигулда в любое время года способна предложить разнообразный отдых своим гостям. На что обратить внимание в Сигулде, чем заняться и как провести время.  ', 'Отдых в Сигулда ', 'Сигулда привлекала первых туристов с середины 19 века. ', '<h2 style=\"text-align: center;\"><strong><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 16px;\">Сигулда - замки, природа, спорт</span></span></strong></h2>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (2, 2, 'Israel/article/stay-in-Tel-Aviv', 'Отдых в Тель-Авиве. ', 'Тель-Авив, отдых, пляжи, море', 'Тель-Авив предлагает туристам серидиземноморские пляжи, колорит Востока, космополитичность и гостипреимство местных жителей!', 'Отдых в Тель-Авиве ', 'Тель-Авив - гостеприимный, веселый,  современный город. ', '<p><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\"><img src=\"/uploads/spool/Израиль.jpg\" alt=\"\" /><em>Тель-Авив</em>, что в переводе с иврита обозначает &laquo;<em>Холм Весны</em>&raquo;, считается центром израильской индустрии и по совместительству &ndash; популярным морским курортом. Город уютно расположился на восточном побережье теплого Средиземного моря &ndash; из-за своего поистине &laquo;тропического&raquo; климата он притягивает туристов круглый год. Каждый день &ndash; начиная с ранней весны и заканчивая поздней осенью &ndash; наполнен в этом месте теплом. Даже в то время, когда европейские страны покрыты снегом, на Средиземноморском побережье <em>Тель-Авива</em> в теплый зимний денек можно заметить большое скопление людей, жаждущих понежиться под лучистым солнышком.</span><br /><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\"><em>Тель-Авив</em> &ndash; это бездна развлечений и открытий, раскинувшиеся километрами золотые пляжи, культурное обогащение, насыщенная ночная жизнь, модные бутики. Выбравшим маршрут своего отдыха в этот замечательный город туристам скучать здесь точно не придется: каждый обязательно найдет для себя занятие по душе.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (3, 1, 'Latvia/article/how-to-get-from-Riga-to-Jurmala', 'Как добраться из Риги до Юрмалы.', 'Рига, Юрмала, такси, электропоезд, маршрутное такси, автобус, велосипед', 'Ригу и Юрмалу разделяют всего полтора десятка километров, однако Рига является большим городом, но добраться до курортной Юрмалы не составляет особого труда.', 'Как добраться из Риги в Юрмалу', 'Доехать из Риги в Юрмалу Вы можете следующим образом ', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"text-decoration: underline;\"><em><strong><img src=\"/uploads/spool/Рига_Юрмала.jpg\" alt=\"\" />На такси из аэропорта</strong></em></span> - 20-30 &euro; (зависит от места назначения и компании осуществляющей перевозки. Самая дешевая - <em>Panda</em>, дорогая - <em>BalticTaxi</em>). </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><strong><span style=\"text-decoration: underline;\"><em>На автомобиле</em></span></strong> &ndash; 25-30 минут. Летом с пятницы по воскресенье бывают &laquo;пробки&raquo; и время в пути увеличивается. Трасса А10 (Вентспилское шоссе) &ndash; 17км. от <em>Риги</em>. Координаты для навигации <em>- 56&deg;58&prime;20&Prime; с.ш. 23&deg;47&prime;49&Prime; в.д</em>. При въезде в город необходимо заплатить налог 1 евро 50 центов (в сутки) за пребывание авто в городе. Штраф &ndash; 60 евро! </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><strong><span style=\"text-decoration: underline;\"><em>На электропоезде</em></span></strong> &ndash; внимание, остановки/платформы <em>&laquo;Юрмала&raquo;</em> не существует! Вам надо взять билет по направлению Тукумс или Слока, до одной из следующих станций:<em> &laquo;Булдури&raquo;</em> (начало города, частный сектор домов), <em>&laquo;Дзинтари&raquo;</em> (концертный зал, центральный пляж), либо <em>&laquo;Майори&raquo;</em> (центр, начало пешеходной улицы Йомас), <em>&laquo;Дубулты&raquo;</em> (самая старая часть курорта, администрация) В дороге &ndash; 30-35 мин. Стоимость проезда 0,90 &euro; . Поезда отправляются каждые 30-40 мин. Контролеры проверяют проездные удостоверения в каждом поезде!</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"text-decoration: underline;\"><em><strong>На автобусе</strong></em></span> - с автовокзала (<em>ул. Прагас д.1</em>) ежедневно совершается порядка 30 рейсов в <em>Юрмалу</em>, время в пути &ndash; около часа. Стоимость одной поездки &ndash; 1,5 &euro; . Учтите, что протяженность <em>Юрмалы</em> по побережью составляет 32 км, а автобус делает на этом расстоянии всего 3 остановки. Определитесь заранее, где удобнее всего Вам будет выходить. Не беспокойтесь, в городе ходит большое количество маршрутных такси.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"text-decoration: underline;\"><em><strong>На теплоходе</strong></em></span> &ndash; с 1 мая по 30 сентября с набережной Даугавы в 11.00 отходит теплоход <em>&laquo;New Wave</em>&raquo; и через 2,5ч. вы причаливаете в <em>&laquo;Майори&raquo;</em>. Цена билета 14 &euro; , поездка в оба конца &ndash; 22 &euro;. </span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"text-decoration: underline;\"><em><strong>На велосипеде</strong></em></span> &ndash; сквозь рижские районы проходит велосипедная трасса до <em>Юрмалы</em>. Имея желание и велосипед (его легко взять на прокат), примерно за час-полтора, по специально оборудованной дорожке, Вы доберетесь до нужного места.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (4, 1, 'Latvia/article/stay-in-Riga', 'Отдых в Риге. Отпуск в Риге. ', 'Рига, Старый город, Даугава, отдых, достопримечательности', 'Отдых в Риге это спокойное, неторопливое времяпрепровождение по весьма умеренным ценам в одной из Европейских ', 'Отдых в Риге', 'Рига всегда пользовалась популярностью у путешественников. ', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em>Отдых в Риге</em> это удивительная возможность посетить один из старинных городов, где исторические памятники архитектуры гармонично соседствуют с ульта-современными зданиями, а жители гороа всегда рады новым гостям! Примечательно и само место, которе <em>древние Ливы</em> выбрали для строительства своего главноего города: точка на карте, где в&nbsp;Рижский залив впадает величественная Даугава и раскинулась столица&nbsp;Латвии.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (5, 4, 'Tatarstan/article/stay-in-Kazan', 'Отдых в Казани. ', 'Казань, отдых, Татарстн, Казанский кремль, ', 'Казань - замечательное место для отдыха: это приветливые люди и богатство истории, неповторимая кухня и комфортабельной проживание.', 'Отдых в Казани', 'Казань - старинный город с современным обликом. ', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\"><em><img src=\"/uploads/spool/Вид_Казань.jpg\" alt=\"\" width=\"433\" height=\"206\" /><span style=\"color: #000000;\">Казань</span></em><span style=\"color: #000000;\"> &ndash; это удивительный город, раскинувшийся на берегах матушки-Волги, он является одним из наиболее важнейших спортивных, образовательных, культурных и политических центров Российской Федерации. Город по праву считается \"третьей\" столицей великой Руси! Чтобы провести свой <em>отдых</em> комфортно и с удовольствием, совсем необязательно ехать заграницу: можно (и нужно) посетить Казань,&nbsp;которая богата своей историей и архитектурой &ndash; для духовного насыщения днем, для вечернего же досуга можно посетить массу развлекательных заведений.&nbsp;</span></span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">В 2005 году <em>Казань</em> отпраздновала свое тысячелетие, при подготовке к торжеству город стал еще лучше и краше: открылись новые торговые и культурно-развлекательные центры международного уровня, отреставрированы памятники архитектуры и другие важные исторические объекты. После привлечения иностранных инвестиций привычный город с мусульманско-православным обликом приобрел только ему присущие&nbsp;черты. Благодаря преобразованиям <em>Казань</em> стала привлекать к себе еще больше внимания со стороны <em>туристов</em> и <em>путешественников</em>. Будучи в этом городе, каждый <em>турист</em> &ndash; независимо от своего вероисповедания &ndash; обязательно должен посетить Храм всех религий. Несмотря на не доведенную пока до конца работу, этот собор уже привлекает как <em>туристов</em>, так и местных жителей: от него попросту невозможно оторвать свой взгляд.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\"><img src=\"/uploads/spool/кремль.jpg\" alt=\"\" /><span style=\"color: #000000;\">Самая главная <em>достопримечательность Казани</em> &ndash; это <em>Казанский кремль</em>, с абсолютной уверенностью это место можно назвать сердцем <em>Татарстана</em>. В нем собраны как русские, так и татарские традиции, которые гармонично сочетаются между собой. Площадь комплекса составляет 1500 кв. м, по всей его территории разбросаны исторические и архитектурные здания: <em>Спасская башня, памятник Мусе Джалилю, мечеть Кул Шариф, Благовещенский собор, падающая башня Сююмбике</em>, <em>Министерство сельского хозяйства</em> и <em>башня Казанского кремля</em>. Описание всей уникальности и богатства этих удивительных мест может занять много времени &ndash; их нужно только видеть! Даже после краткого обзора становится очевидно, что находясь на <em>отдыхе в Казани</em>, обязательно стоит посетить это место.</span></span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Одной из важных культурных достопримечательностей города является <em>Академический театр имени Качалова</em>. Комплекс можно смело назвать самым главным храмом&nbsp;искусства в <em>Казани</em>: он существует уже более чем 200 лет. На этой сцене актеры всегда вели открытую беседу со зрителями на самые разные темы: до сих пор этот дух сохранился в этом месте, а спектакли, как и прежде, собирают аншлаги. В театре чаще всего ставят произведения, ставшие классикой: по традиции, это пьесы Пушкина и Чехова. Кроме участия в постановках классических произведений, талантливые актеры труппы театра играют по пьесам Булгакова, Вайля, Брехта и Катаева, спектакли современных авторов также не остались без внимания.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Узнать еще больше о великой и неповторимой России можно, приехав в город <em>Казань</em>. Желательно планировать свое путешествие на несколько дней, ведь кроме описанных выше <em>достопримечательностей</em>, здесь много интересных мест описание которых не смогла вместить эта статья. Перечилим коротко: <em>Национальный музей Татарстана, Дворец земледельцев, Казанский государственный музей юного зрителя, Камерный Шаляпинский зал, Кот Казанский, Литературно-Мемориальный музей Горького, мечети Булгар и Марджани, мост Миллениум</em>, прочие.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (6, 2, 'Israel/articles/treatment-at-the-Dead-sea', 'Лечение на Мертвом море: основные рекомендации, противопоказания.', 'лечение на Мертвом море, Эйн-Бокека, грязелечение на Мертвом море', 'Лечение на Мертвом море выбирает для себя большое количество людей. Это оснащенные по последнему слову техники клиники, лучшие врачи и первоклассный сервис. ', 'Лечение на Мертвом море в Израиле.', 'Клиники находящиеся у Мертвого моря одни из лучших в мире.', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img src=\"/uploads/spool/Мертвое_море.jpg\" alt=\"\" />О целебных свойствах воды и климатических условий <em>Мертвого моря</em> можно говорить бесконечно! За счет высокой концентрации солей тело находится в невесомости. Когда ложишься на водную гладь и&hellip; паришь! Никаких телодвижений, манипуляций &ndash; только спокойствие во всем теле, абсолютная расслабленность и ощущение, что рождаешься заново. Не умеющим плавать, но мечтающим подрейфовать на поверхности воды &ndash; идеальный вариант!</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Утонуть невозможно, просто нереально. Даже, если поэкспериментировать и попытаться нырнуть &ndash; этого не получится сделать &ndash; вода вытолкнет тело обратно на поверхность.&nbsp;</span><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Да, и постоянно дежурящие на берегу спасатели будут усердно, со свойственным евреям спокойствием и доброжелательностью повторять/напоминать/объяснять, что нырять нельзя во избежание проблем с собственным же здоровьем. Банальный пример: если вода <em>Мертвого моря</em> попадет в глаза, то единственно возможный вариант избавиться от сильнейшего чувства жжения &ndash; это промыть глаза пресной водой. А для этого придется, как минимум бежать на берег, максимум (при отсутствии с собой бутилированной воды) &ndash; в отель. Кстати, все отели и здравницы сконцентрированы в курортной зоне <em>Эйн-Бокека</em>. </span><br /><span style=\"font-size: 12pt; font-family: \'arial black\', \'avant garde\'; color: #000000;\">интересные факты о лечении на Мертвом море</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img src=\"/uploads/spool/вода.jpg\" alt=\"\" />- вдоль побережья <em>Мертвого моря</em> располагаются термальные источники с сероводородом &ndash; веществом, которое активно используется в медицинских клиниках Эйн-Бокека, а также в СПА-салонах для проведения оздоровительных, лечебных и косметических процедур;</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- сероводород оказывает благоприятное воздействие на кровеносную систему, способствует укреплению иммунитета, улучшению процесса метаболизма;</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- в воздушном пространстве на территории курорта концентрация брома превышает обычную более чем в двадцать раз, таким образом, находясь на территории курортной зоны, человек как бы делает круглосуточные лечебные ингаляции а это ничто иное, как профилактика заболеваний дыхательной системы, в особенности астмы;</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- количество кислорода в воздухе вблизи <em>Мертвого моря</em> на пятнадцать процентов выше, чем в любом другом месте земного шара;</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- очень высокое атмосферное давление, которое в совокупности с повышенной концентрацией кислорода создает невероятный эффект барокамеры в природе;</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- ультрафиолетовые лучи не оказывают пагубного воздействия на кожу &ndash; можно загорать сколько угодно и в любое удобное время суток. Вредный ультрафиолет категории \"А\" преобразуется в категорию полезных лучей \"Б\";</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- о целебной силе вод <em>Мертвого моря</em> знали еще древние цари, Клеопатра, Ирод и Аристотель;</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">- богатые римляне использовали соленую воду для лечения множества заболеваний.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (7, 1, 'Latvia/article/the-beaches-of-Jurmala', 'Пляжи Юрмалы. Описание, особенности. ', 'Юрмала, пляжи, Майори, пляжи Юрмалы, Латвия, Яункемери', 'Пляжи Юрмалы - это более 30 километров береговой линии, где каждый сможет найти себе место и занятие по желанию. ', 'Пляжи Юрмалы', 'Любой отдыхающий в этом городе выберет себе свой пляж!', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img src=\"/uploads/spool/Пляж.jpg\" alt=\"\" />Курортной столицей <em>Латвии</em> давно стали считать именно <em>Юрмалу</em>. Данный городок отличается своей неповторимой атмосферой и красотой. Инфраструктура здесь достаточно высокоразвита, несмотря на то, что в городе нет больших небоскребов, бизнес-центров и во всем прочем он уютный и тихий. Вы найдете множество зданий, которые прямо-таки поразят своей красотой и замысловатой архитектурой. Благодаря своему географическому расположению, город может считается оздоровительным курортом, поскольку предоставит массу возможностей полюбоваться разнообразием природы и набраться сил.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (8, 9, 'Belarus/article/Belovezhskaya-food', 'Беловежская пуща. Беларусь. ', 'Беловежская пуща, заповедник, национальный парк, музей природы, дед мороз', 'Беловежская пуща - является Государственным Национальным Парком Республики Беларусь, включена в список объектов всемирного наследия ЮНЕСКО.', 'Беловежская пуща', 'Беловежская пуща - уникальный природный объект в Беларуси.', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em><img src=\"/uploads/spool/пуща61.jpg\" alt=\"\" width=\"314\" height=\"247\" />Беловежская пуща</em>&hellip; Сколько стихов, рассказов было написано о национальном заповеднике Республики Беларусь, сколько песен спето о единственном месте в Европе, где проживают невероятные по своей красоте, мощи и силе животные &ndash; зубры.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Белорусы гордятся Беловежской пущей, делают все, чтобы сохранить заповедник и приумножить богатства растительного и животного мира, большинство из которых находится в Красной Книге РБ. Уже тринадцать лет <em>Беловежская пуща</em> относится к объектам, включенным в список всемирного наследия человечества ЮНЕСКО.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Территория Пущи располагается в трех районах страны: Пружанском, Каменецком и Свислочском (Брестская и Гродненская область). Через заповедник проходит граница между Беларусью и Польшей. В непосредственной близости от парка - водораздел Черного и Балтийского морей. На территории действуют четыре зоны, каждая из которых имеет свой режим охраны. Вокруг всей пущи есть буферная зона, где также ведется охрана.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Название \"Беловежская пуща\" происходит от названия близлежащего населенного пункта - Беловежа. Хотя некоторые считают, что свое имя заповедник получил благодаря Замку в Каменце, который назывался \"Белая Вежа\".</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Основная территория парка (около 86%) лесной массив с преобладанием сосновых боров. Более пятидесяти процентов деревьев на территории заповедника в возрасте от 250 до 350 лет, диаметр ствола большинства экземпляров достигает полутора метров в обхвате. Есть деревья, обхватить которые могут только четыре человека, взявшись за руки.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Если хочется собственными глазами увидеть, где было подписано историческое Соглашение, приведшее к распаду легендарного Союза Советских Социалистических Республик, то обязательно стоит посетить резиденцию Вискули, которая до сих пор сохранилась.</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (9, 4, 'Tatarstan/article/best-city-in-Tatarstan', 'Лучший город в Республике Татарстан', 'Казань, Татарстан, Набережные Челны, Елабуга, Бугульма', 'Лучший город в Татарстане определить сложно - все города республики хороши и каждый из них по своему. В этой статье мы постараемся ответить на этот вопрос ни кого не обидив. ', 'Лучший город Татарстана', 'Постараемся ответить на этот сложный вопрос. ', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em>Республика Татарстан</em> является значимым членом Российской Федерации. После распада Советского союза Татарстан, как и вся страна, переживал тяжёлые времена. Умелое руководство смогло достаточно оперативно стабилизировать ситуацию и тем самым обеспечило быстрый экономический рост. Как следствие экономического подъема стали обновляться города республики: появились современные жилые комплексы, успешно развивается инфраструктура, осваиваются пустующие или заброшенные территории.&nbsp;</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (10, 2, 'Israel/articles/stay-at-the-Dead-sea-in-El-Bokek', 'Отдых на Мертвом море в Эль-Бокеке, Израиль.', 'Эль-Бокек, Мертвое море, отели, пляжи, магазины, еда', 'Отдых на Мертвом море - описание отелей, особенностей отдыха, о местечке Эль-Бокек, чем заняться, достопримечательности и многое другое.', 'Отдых на Мертвом море в местечке Эль-Бокек', 'Отдых на Мертвом море можно спланировать в любое время года.', '<h2 style=\"text-align: center;\"><span style=\"font-size: 14pt; font-family: \'arial black\', \'avant garde\'; color: #000000;\">Интересные факты о Мертвом море и о территории Эйн-Бокека, где оно находится</span></h2>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (11, 1, 'Latvia/article/sanatorium-Latvia', 'Санатории Латвии. Описание, адреса, контакты. ', 'санатории, курортно-реабилитационный центр, Юрмала, Латвия, СПА-отели', 'Санатории Латвии пользуются заслуженной популярностью благодаря уникальному морскому воздуху в сочетании с чистейшими хвойными лесами и обслуживанием по европейским стандартам.', 'Санатории Латвии', 'Благодаря высоким стандартам санатории Латвии популярны.', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img src=\"/uploads/spool/Санатории1.jpg\" alt=\"\" width=\"381\" height=\"286\" />Первые санатории Латвии появились еще в 19 веке, когда воды Балтики приобрели широкую известность, их считали святыми. С 1801 года ученые стали изучать химические свойства латвийских минеральных вод, что послужило признанием Юрмалы одним из лучших курортных городов. Высшие слои общества приезжали сюда, чтобы укрепить свое здоровье. До сих пор мало, что изменилось: сюда ежегодно прибывает огромное количество людей, преследующих эти же цели.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Уникальная чистота воздуха, мягкость климата близ Балтийского моря, качество обслуживания, оздоровления, лечения и реабилитации в здравницах Латвии известна далеко за ее пределами. Большинство санаториев, домов отдыха, гостиниц расположены в курортных зонах, где хвойный лес сочетается с близостью морского побережья.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Территория курортных городов Латвии богата сероводородными источниками и запасами лечебных грязей, которые успешно применяются для лечения заболеваний различных систем организма.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Каждый санаторий, отель, предлагающий определенные оздоровительные программы, имеет новейшее медицинское оборудование для их реализации и большой штат высококвалифицированных докторов.</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Особенно радуют туристов, приезжающих в Латвию с целью оздоровления, весьма приемлемые и очень привлекательные цены курортов. При этом качество медицинского обслуживания, условия проживания ничуть не хуже, чем в лучших здравницах других стран. Красивейшие природные ландшафты, песчаные пляжи с сосновыми дюнами, чистый воздух, умеренный климат, изысканная латвийская кухня сделают ваш отпуск чрезвычайно полезным и самым незабываемым!</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (12, 14, 'Turkey/article/the-attractions-of-Turkey', 'Достопримечательности Турции. Описание, история.', 'Достопримечательности Турции, Стамбул, Анкара, Алания, Кемер, Белек, Анталия, Мармарис, Эфес', 'Достопримечательности Турции - описание основных достопримечательностей Турецких городов, краткая историческая справка. ', 'Достопримечательности Турции', 'Турция - страна с богатейшим культурным прошлым. ', '<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img src=\"/uploads/spool/турция.jpg\" alt=\"\" width=\"329\" height=\"246\" />В <em>Турции</em>&nbsp;озле каждого города найдется масса <em>достопримечательностей</em>, которые будут интересны всем: и любителям древностей, истории, архитектуры, и тем, кто любит природу. Просто диву даешься от того, сколько здесь по настоящему уникальных, удивительно красивых памятников природы, древних сооружений, великолепных архитектурных построек. Вы только себе представьте, по официальным данным в стране имеется около 2600 древних городов, из которых около 600 все еще находятся под землей - их раскопки еще не начинались! А каково богатство природного мира &ndash; это просто сказка!</span><br /><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Места, которые обязательно нужно посетить!</span></p>
insert into `tourism_article` (`id`, `parent_id`, `inner_name`, `title`, `keywords`, `meta_description`, `name`, `short_description`, `description`) values (13, 2, 'Israel/article/Eilat-Attractions', 'Достопримечательности и экскурсии в Эйлате. ', 'достопримечательности, экскурсии, Эйлат, Израиль', 'Эйлат - не смотря, что город является морским курортом, в Эйлате много достопримечательностей и организованных экскурсионным маршрутов по интересным местам. ', 'Достопримечательности и экскурсии в Эйлате', 'Эйлат - предлагает туристам не только пляжный отдых. ', '<h2 style=\"text-align: center;\"><span style=\"font-size: 14pt; font-family: \'arial black\', \'avant garde\'; color: #000000;\">Достопримечательности Эйлата</span></h2>
