CREATE TABLE IF NOT EXISTS `tourism_banner` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `parent_id` int(10) unsigned NOT NULL,
  `company_name` varchar(255) DEFAULT NULL,
  `contact_fio` varchar(255) DEFAULT NULL,
  `contact_phone` varchar(128) DEFAULT NULL,
  `contact_email` varchar(255) DEFAULT NULL,
  `expiration_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `alt_text` varchar(25) DEFAULT NULL,
  `href` varchar(255) DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `show_count` int(10) DEFAULT NULL,
  `click_count` int(10) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`),
  CONSTRAINT `tourism_banner_ibfk_1` FOREIGN KEY (`parent_id`) REFERENCES `tourism_country` (`id`) ON DELETE CASCADE ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

